import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const handleAddToCart = (product) => {
    setCartItems((prevItems) => {
      const itemExists = prevItems.find((item) => item.id === product.id);
      if (itemExists) {
        return prevItems.map((item) =>
            item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  const handleRemoveFromCart = (id) => {
    setCartItems((prevItems) => prevItems.filter((item) => item.id !== id));
  };

  const handleCheckout = () => {
    alert('Comanda a fost finalizată!');
    setCartItems([]);
  };

  const products = [
    { id: 1, name: 'Produs 1', price: 100, image: '/images/produs1.jpg', description: 'Descriere produs 1' },
    { id: 2, name: 'Produs 2', price: 150, image: '/images/produs2.jpg', description: 'Descriere produs 2' },
    // ... alte produse
  ];

  return (
      <Router>
        <Routes>
          <Route path="/" element={<ProductList products={products} handleAddToCart={handleAddToCart} />} />
          <Route path="/cart" element={<Cart cartItems={cartItems} handleRemoveFromCart={handleRemoveFromCart} handleCheckout={handleCheckout} />} />
        </Routes>
      </Router>
  );
};

export default App;
