import axios from "./axios";

// Fetch all items in the cart for the current user
const getCartItems = (userId) => {
    const token = localStorage.getItem("token"); // Use the token for authenticated requests
    return axios.get(`/api/cos/${userId}`, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });
};

// Add an item to the user's cart
const addCartItem = (productId, userId) => {
    const token = localStorage.getItem("token");
    return axios.post(
        "/api/cos/adauga",
        null, // Body is not needed since the backend expects request parameters
        {
            params: { produsId: productId, utilizatorId: userId }, // Send parameters as query params
            headers: {
                Authorization: `Bearer ${token}`,
            },
        }
    );
};

// Remove an item from the cart (assuming there's a feature for this; requires endpoint in backend)
const removeCartItem = (itemId) => {
    const token = localStorage.getItem("token");
    return axios.delete(`/api/cos/remove/${itemId}`, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });
};

export default {
    getCartItems,
    addCartItem,
    removeCartItem,
};
