import axios from "axios";

// Base URL for your backend API
const BASE_URL = "http://localhost:8080/api"; // Update this based on your backend configuration

// Create an axios instance with default settings
const axiosInstance = axios.create({
    baseURL: BASE_URL,
    headers: {
        "Content-Type": "application/json",
    },
});

// Add a response interceptor (optional, for error handling or authentication token headers)
axiosInstance.interceptors.response.use(
    (response) => response,
    (error) => {
        // Handle errors globally, like redirecting to login on 401
        if (error.response && error.response.status === 401) {
            console.error("Unauthorized - You need to log in.");
        }
        return Promise.reject(error);
    }
);

export default axiosInstance;
