import React, { useEffect, useState } from "react";
import CartService from "../services/CartService";

export default function Cart() {
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        // Fetch the user ID from localStorage or any other source
        const userId = localStorage.getItem("userId"); // Assuming you save the user ID in localStorage

        if (userId) {
            // Fetch cart items when the page loads for the specific user
            CartService.getCartItems(userId)
                .then((response) => {
                    setCartItems(response.data);
                })
                .catch((error) => {
                    console.log("Error fetching cart items:", error);
                });
        } else {
            console.error("User ID is not available. Ensure the user is logged in.");
        }
    }, []);

    const handleRemoveFromCart = (itemId) => {
        CartService.removeCartItem(itemId)
            .then(() => {
                setCartItems(cartItems.filter(item => item.id !== itemId));
            })
            .catch((error) => {
                console.log("Error removing item:", error);
            });
    };

    return (
        <div className="container mt-4">
            <h2>Coșul Tău</h2>
            {cartItems.length === 0 ? (
                <p>Coșul tău este gol.</p>
            ) : (
                <ul className="list-group">
                    {cartItems.map((item) => (
                        <li className="list-group-item d-flex justify-content-between align-items-center" key={item.id}>
                            {item.productName}
                            <span>${item.productPrice}</span>
                            <button
                                className="btn btn-danger btn-sm"
                                onClick={() => handleRemoveFromCart(item.id)}
                            >
                                Remove
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
