import React from 'react';

const Cart = ({ cartItems, handleRemoveFromCart, handleCheckout }) => {
    const calculateTotal = () =>
        cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

    return (
        <div className="cart">
            <h2>Coșul de Cumpărături</h2>
            {cartItems.length === 0 ? (
                <p>Coșul este gol.</p>
            ) : (
                <div>
                    {cartItems.map((item) => (
                        <div key={item.id} className="cart-item">
                            <img src={item.image} alt={item.name} className="cart-item-image" />
                            <div>
                                <h4>{item.name}</h4>
                                <p>Preț: ${item.price}</p>
                                <p>Cantitate: {item.quantity}</p>
                                <button onClick={() => handleRemoveFromCart(item.id)}>Șterge</button>
                            </div>
                        </div>
                    ))}
                    <h3>Total: ${calculateTotal().toFixed(2)}</h3>
                    <button onClick={handleCheckout} className="checkout-button">Finalizează Comanda</button>
                </div>
            )}
        </div>
    );
};

export default Cart;
