import React, { useState, useEffect } from "react";

function ProductList() {
    const [products, setProducts] = useState([]); // State to hold the product data
    const [error, setError] = useState(null); // State to hold any errors

    useEffect(() => {
        // Fetch product data from the backend API
        fetch("http://localhost:8080/api/produse")
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                return response.json(); // Parse data as JSON
            })
            .then((data) => {
                setProducts(data); // Set the retrieved data to the products state
            })
            .catch((error) => {
                console.error("Error fetching product data:", error);
                setError(error.message); // Set error message in case of failure
            });
    }, []);

    // Render the product list or handle errors/loading
    if (error) {
        return <div>Error: {error}</div>; // Display error message if API call fails
    }

    return (
        <div>
            <h1>Product List</h1>
            {products.length > 0 ? (
                <ul>
                    {products.map((product) => (
                        <li key={product.id}>
                            <strong>{product.nume}</strong> - {product.pret} RON
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Loading products...</p> // Display a loading message until data is fetched
            )}
        </div>
    );
}

export default ProductList;
