import axios from "./axios";

const getCartItems = () => {
    const token = localStorage.getItem("token"); // Use the token for authenticated requests
    return axios.get("/cart", {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });
};

const addCartItem = (productId, quantity = 1) => {
    const token = localStorage.getItem("token");
    return axios.post(
        "/cart",
        { productId, quantity },
        {
            headers: {
                Authorization: `Bearer ${token}`,
            },
        }
    );
};

const removeCartItem = (itemId) => {
    const token = localStorage.getItem("token");
    return axios.delete(`/cart/${itemId}`, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });
};

export default {
    getCartItems,
    addCartItem,
    removeCartItem,
};
