import React, { useEffect, useState } from "react";
import ProductCard from "../components/ProductCard";
import ProductService from "../services/ProductService";

export default function Home() {
    const [products, setProducts] = useState([]); // Initialize as an empty array
    const [errorMessage, setErrorMessage] = useState("");

    useEffect(() => {
        ProductService.getAllProducts()
            .then((response) => {
                console.log("API Response:", response.data); // Inspect the response data
                if (Array.isArray(response.data)) {
                    setProducts(response.data); // Set state only if data is an array
                } else {
                    setErrorMessage("Unexpected response format from API.");
                    console.error("Unexpected data format:", response.data);
                }
            })
            .catch((error) => {
                console.error("Error fetching products:", error);
                setErrorMessage("Failed to load products.");
            });
    }, []);

    const handleAddToCart = (productId) => {
        console.log(`Product ${productId} added to cart!`);
        // TODO: Add logic to update the cart using a CartService
    };

    if (errorMessage) {
        // Display an error message if something goes wrong
        return <div className="container mt-4"><p>{errorMessage}</p></div>;
    }

    return (
        <div className="container mt-4">
            <h1>Product List</h1>
            <div className="row">
                {products.length > 0 ? (
                    products.map((product) => (
                        <div className="col-md-4 mb-3" key={product.id}>
                            <ProductCard product={product} onAddToCart={handleAddToCart} />
                        </div>
                    ))
                ) : (
                    <p>No products available.</p>
                )}
            </div>
        </div>
    );
}
