import axios from "./axios";

const getAllProducts = () => {
    return axios.get("/api/produse"); // Correct endpoint to fetch products
};

const getProductById = (id) => {
    return axios.get(`/api/produse/${id}`); // Correct endpoint to fetch a product by ID (if it exists on the backend)
};

export default {
    getAllProducts,
    getProductById,
};
