import React, { useState } from "react";
import axios from "../services/axios";

export default function Login() {
    const [credentials, setCredentials] = useState({ email: "", password: "" }); // Changed from "username" to "email"
    const [error, setError] = useState(null);

    const handleSubmit = (event) => {
        event.preventDefault();
        setError(null); // Reset error before submitting

        // Send login request to the backend
        axios.post("/auth/login", credentials)
            .then((response) => {
                localStorage.setItem("token", response.data.token); // Saves the token

                alert("Login successful!");
                window.location.href = "/"; // Redirect to home after login
            })
            .catch((err) => {
                if (err.response && err.response.status === 401) {
                    setError("Invalid email or password");
                } else {
                    setError("An unexpected error occurred. Please try again.");
                }
            });
    };

    return (
        <div className="container mt-4">
            <h2>Login</h2>
            {error && <p className="text-danger">{error}</p>} {/* Error message display */}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Email</label> {/* Changed from "Username" to "Email" */}
                    <input
                        type="email" // Changed input type to "email"
                        className="form-control"
                        value={credentials.email} // Bind to "email" state
                        onChange={(e) =>
                            setCredentials({ ...credentials, email: e.target.value })
                        }
                    />
                </div>
                <div className="form-group">
                    <label>Password</label>
                    <input
                        type="password"
                        className="form-control"
                        value={credentials.password} // Bind to "password" state
                        onChange={(e) =>
                            setCredentials({ ...credentials, password: e.target.value })
                        }
                    />
                </div>
                <button type="submit" className="btn btn-primary">
                    Login
                </button>
            </form>
        </div>
    );
}
