import axios from "./axios";

const getCartItems = (userId) => {
    const token = localStorage.getItem("token");
    return axios.get(`/cos/${userId}`, { // Base URL already includes `/api`
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });
};

const addCartItem = (productId, userId) => {
    const token = localStorage.getItem("token");
    return axios.post(
        "/cos/adauga", // Base URL already includes `/api`
        null,
        {
            params: { produsId: productId, utilizatorId: userId },
            headers: {
                Authorization: `Bearer ${token}`,
            },
        }
    );
};

const removeCartItem = (itemId) => {
    const token = localStorage.getItem("token");
    return axios.delete(`/cos/remove/${itemId}`, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });
};

export default {
    getCartItems,
    addCartItem,
    removeCartItem,
};
